/*
 * jQuery图片轮播(焦点图)插件
 * Version: 0.2.0518
 * Author: K
 */
(function($) {
    $.fn.slideBox = function(options) {
        //默认参数
        var defaults = {
            direction : 'left',//left,top
            duration : 7,//unit:seconds
            easing : 'swing',//swing,linear
            delay : 7,//unit:seconds
            startIndex : 0,
            hideClickBar : true,
//            clickBarRadius : 10,//unit:px//点的圆角度数//用css控制
            hideBottomBar : false,
            width : null,
            height : null,
            width100Proc:1
        };
        var settings = $.extend(defaults, options || {});
//		var brown_width = $(settings.showName).width();
//        alert(settings.width);
        if (settings.width100Proc =="1"){
            settings.width=100;
        }
        var brown_width = settings.width;
        //计算相关数据
        var wrapper = $(this), ul = wrapper.children('ul.items'), lis = ul.find('li'), firstPic = lis.first().find('img');
        var li_num = lis.size(), li_height = 0, li_width = 0;
        //定义滚动顺序:ASC/DESC.ADD.JENA.201208081718
        var order_by = 'ASC';
        //初始化
        var init = function(){
            if(!wrapper.size()) return false;
            //手动设定值优先.ADD.JENA.201303141309
            li_height = settings.height ? settings.height : firstPic.first().height();
            li_width = settings.width ? settings.width : lis.first().width();

			$(settings.showName).height(li_height);
            if (settings.width100Proc =="1"){
                wrapper.css({width: li_width+'%', height:li_height+'px'});
                lis.css({width: li_width/li_num+'%', height:li_height+'px'});
            }else{
                wrapper.css({width: li_width+'px', height:li_height+'px'});
                lis.css({width: li_width+'px', height:li_height+'px'});
            }

            if (settings.direction == 'left') {
                if (settings.width100Proc =="1"){
                    ul.css('width', li_num * brown_width +'%');
                }else{
                    ul.css('width', li_num * brown_width + 'px');
                }
            } else {
                ul.css('height', li_num * li_height + 'px');
            }
            ul.find('li:eq('+settings.startIndex+')').addClass('active');

            if(!settings.hideBottomBar){//ADD.JENA.201208090859
                var tips = $('<div class="tips"></div>').css('opacity', 1).appendTo(wrapper);

                var nums = $('<div class="nums"></div>').hide().appendTo(tips);
                lis.each(function(i, n) {
                    var a = $(n).find('a'), text = a.attr('title'), href = a.attr('href'), css = '';
                    i == settings.startIndex && (css = 'active');
                    $('<a>').attr('href', href).text(text).addClass(css).mouseover(function(){
                        $(this).addClass('active').siblings().removeClass('active');
                        ul.find('li:eq('+$(this).index()+')').addClass('active').siblings().removeClass('active');
                        start();
                        stop();
                    }).appendTo(nums);
                });
                nums.show();

            }

            lis.size()>1 && start();
        }
        //开始轮播
        var start = function() {
            var active = ul.find('li.active'), active_a = active.find('a');
            var index = active.index();
            if(settings.direction == 'left'){
                if (settings.width100Proc =="1"){
                    offset = index * brown_width * -1;
                    param = {'left':offset + '%'};
                }else{
                    offset = index * brown_width * -1;
                    param = {'left':offset + 'px' };
                }
            }else{
                offset = index * li_height * -1;
                param = {'top':offset + 'px' };
            }

            wrapper.find('.nums').find('a:eq('+index+')').addClass('active').siblings().removeClass('active');
            wrapper.find('.title').find('a').attr('href', active_a.attr('href')).text(active_a.attr('title'));

            ul.stop().animate(param, settings.duration*1000, settings.easing, function() {
                active.removeClass('active');
                if(order_by=='ASC'){
                    if (active.next().size()){
                        active.next().addClass('active');
                    }else{
                        order_by = 'DESC';
                        active.prev().addClass('active');
                    }
                }else if(order_by=='DESC'){
                    if (active.prev().size()){
                        active.prev().addClass('active');
                    }else{
                        order_by = 'ASC';
                        active.next().addClass('active');
                    }
                }
            });
            wrapper.data('timeid', window.setTimeout(start, settings.delay*1000));

            //alert(active.find('img').attr("b"));
            /*switch(index){
             case 0:
             $(".lunbo").css("background-image","url(../images/bg.jpg)");
             break;
             case 1:
             $(".lunbo").css("background","#f8f3ed");
             break;
             default:
             $(".lunbo").css("background-image","url(../images/bg.jpg)");
             }*/
        };
        //停止轮播
        var stop = function() {
            window.clearTimeout(wrapper.data('timeid'));
        };
        //鼠标经过事件
        wrapper.hover(function(){
            stop();
        }, function(){
            window.setTimeout(start, settings.delay*1000);//ADD.JENA.201303141309
        });
        //首张图片加载完毕后执行初始化
        var imgLoader = new Image();
        imgLoader.onload = function(){
            imgLoader.onload = null;
            init();
        }
        imgLoader.src = "themes/gcake/images_xin/lunbo1.jpg";

    };
})(jQuery);